import './home.css';

// import { useFetch } from '../../hooks/useFetch';
import { useState, useEffect} from 'react/cjs/react.production.min';
const Home = () => {
    const [cata, setCata] = useState([])
    // const [loading, setLoading] = useState(true)
    // const {data,isPending,error } = useFetch('https://www.themealdb.com/api/json/v1/1/categories.php')
    useEffect(() => {
        fetch('https://www.themealdb.com/api/json/v1/1/categories.php')
            .then(response => {
                if (response.ok) {
                    return response.json()
                }
                throw response;
            })
            .then(json => {
                setCata(json)
            });
    
    }, []);

    console.log(cata)
    return (
        <div>
            {cata}
            {/* {error && <p className='error'>{error}</p>}
            {isPending && <p className='loading'>Lodding...</p>}
            {
                data && data.map(categories => (
                    < h2 key={categories.idCategory} >{categories.strCategory} </h2>

            ))} */}
        </div>
    );
}

export default Home;
