import './home.css';

import { useFetch } from '../../hooks/useFetch';
import { useState } from 'react/cjs/react.production.min';
const Home = () => {
const [cata, setCata] = useState(null)
    const {data,isPending,error } = useFetch('https://www.themealdb.com/api/json/v1/1/categories.php')

  fetch('https://www.themealdb.com/api/json/v1/1/categories.php')
      .then(response => response.json())
        .then(data => setCata(data));
    console.log(setCata(data))
    return (
        <div>
            {error && <p className='error'>{error}</p>}
            {isPending && <p className='loading'>Lodding...</p>}
            {
                data && data.map(categories => (
                    < h2 key={categories.idCategory} >{categories.strCategory} </h2>

            ))}
        </div>
    );
}

export default Home;
