import './home.css';

import { useFetch } from '../../hooks/useFetch';
const Home = () => {

    const {
        data,
        isPending,
        error
    } = useFetch('https://www.themealdb.com/api/json/v1/1/categories.php')

  
    return (
        <div>
            {error && <p className='error'>{error}</p>}
            {isPending && <p className='loading'>Lodding...</p>}
            {data && data.map(strCategory => (
                <h2 key={strCategory.idMeal}>{strCategory.strMeal}</h2>

            ))}
        </div>
    );
}

export default Home;
